const (
	proposalV3Code = ""
)

type forkProposalV3 struct {
}

func (f *forkProposalV3) GetName() string {
	return ProposalV3ContractName
}

func (f *forkProposalV3) Update(config *params.ChainConfig, height *big.Int, state *state.StateDB) (err error) {
	contractCode := common.FromHex(proposalV3Code)

	//write code to sys contract
	state.SetCode(ProposalV3ContractAddr, contractCode)
	log.Debug("Write code to system contract account", "addr", ProposalV3ContractAddr.String(), "code", proposalV3Code)

	return
}

func (f *forkProposalV3) Execute(state *state.StateDB, header *types.Header, chainContext core.ChainContext, config *params.ChainConfig) (err error) {

	v2 := NewValidatorV2()
	topVals, err := v2.GetTopValidators(state, header, chainContext, config)
	if err != nil {
		log.Error("getTopValidators from V2 failed", "err", err)
		return err
	}

	method := "initialize"
	data, err := GetInteractiveABI()[ProposalV3ContractName].Pack(method, topVals)
	if err != nil {
		log.Error("Can't pack data for initialize", "error", err)
		return err
	}

	msg := types.NewMessage(header.Coinbase, &ProposalV3ContractAddr, 0, new(big.Int), math.MaxUint64, new(big.Int), data, false)
	_, err = caller.ExecuteMsg(msg, state, header, chainContext, config)

	return
}
